INSERT INTO `mq_pingjia` VALUES ('139', '92', '9', '20', '6', '10', '10', '10', '65', '平台较广，原始森林一般，可以施展抱负', '管理效率低，人治高于机制，人事复杂，压力大，工作量大，加班严重。 建议尊重人力资源，提高员工福利。', '82', '2011', '1', null, '2012-08-26 00:04:44', '182.40.158.249');
