INSERT INTO `mq_pingjia` VALUES ('151', '63', '16', '12', '12', '12', '12', '12', '76', '宝山钢铁股份有限公司（简称“宝钢股份”）是中国最大、最现代化的钢铁联合企业。宝钢股份以其诚信、人才、创新、管理、技术诸方面综合优势-奠定了在国际钢铁市场上世界级钢铁联合企业的地位。', '相类似的部门很多 ，除钢铁主业外，其他的例如矿业、运输等配套都做的不好！ 建议以解决职工群众最关心、最直接、最现实的利益问题为重点', '109', '2010', '4', null, '2012-08-26 00:06:07', '182.40.158.249');
