INSERT INTO `mq_pingjia` VALUES ('264', '15', '20', '16', '15', '15', '15', '15', '96', '宽松的环境，有喝不完的酸奶，每天3点还有水果呢，早上上班不用担心迟到', '牛人太多啊，随便一个都是很聪明的人，要么技术牛，要么研究能力强 建议再接再厉，争取早日统一江湖，称霸武林，bing能够一鸣惊人', '111', '2009', '1', null, '2012-08-26 14:29:59', '182.40.162.85');
