INSERT INTO `mq_pingjia` VALUES ('542', '297', '12', '16', '10', '10', '10', '12', '70', '工作环境好，有年假，投行背景，周围人素质普遍较好', '加班多，没有加班费，事情多，老板比较苛刻 建议加工资，增加公司福利，给员工更多培训机会', '79', '2012', '2', null, '2012-08-28 20:42:43', '182.40.162.179');
INSERT INTO `mq_pingjia` VALUES ('543', '61', '9', '12', '10', '10', '10', '10', '61', '吃皇粮，国家补贴，有垄断优势，品牌知名度高', '老国企，激励机制不明确，找关系的很多，工资很死 建议引进竞争机制，多外聘，提成加高，多培训，提升员工素质', '127', '2010', '2', null, '2012-08-28 20:43:10', '182.40.162.179');
INSERT INTO `mq_pingjia` VALUES ('544', '3', '6', '16', '6', '8', '6', '6', '48', '编制内人员待遇较好，工作无压力。', '对于外聘员工没有任何的升迁和学习机会。新派驻的员工基本无年休。外聘员工基本上都是替罪羔羊。按资排辈。工作效率拖沓-绩效评分全靠领导印象。每隔几年一次的组织结构变更-很多不适合的领导被分配到需要专业素养较高的部门。建议外派制度的存在很难改善公。', '111', '2012', '1', null, '2012-08-28 20:43:45', '182.40.162.179');
INSERT INTO `mq_pingjia` VALUES ('545', '25', '9', '12', '10', '10', '10', '10', '61', '1-公司的福利还好2-培训机会多', '一个人干两个人的活，工资跑不过 CPI ，人员流动大', '125', '2011', '1', null, '2012-08-28 20:44:08', '182.40.162.179');
