INSERT INTO `mq_pingjia` VALUES ('821', '66', '9', '20', '8', '8', '8', '10', '63', '好多机会，公司对员人们好，不错！认真地工作，整理很好！', '夜班很正常  我会好好 建议工作正常', '156', '2011', '2', null, '2012-08-30 21:49:31', '182.40.161.132');
