INSERT INTO `mq_pingjia` VALUES ('823', '209', '6', '20', '6', '6', '8', '6', '52', '没有什么优点1待遇差2没福利3领导无能4工作压力极大5工作无乐趣', '1待遇差2没福利3领导无能4工作压力极大5工作无乐趣 建议趁早关门吧！！！！！！', '141', '2012', '1', null, '2012-08-30 21:50:45', '182.40.161.132');
