INSERT INTO `mq_pingjia` VALUES ('832', '76', '20', '20', '15', '15', '15', '15', '100', '行业领导者，拥有强大的市场份额', '应该提高福利，更多的员工激励计划，以及员工成长机会。 建议应该提高福利，更多的员工激励计划，以及员工成长机会。', '168', '2012', '1', null, '2012-08-31 21:10:12', '113.124.15.146');
