INSERT INTO `mq_pingjia` VALUES ('917', '18', '16', '12', '12', '12', '12', '12', '76', '1、工作轻松；2、工作压力相对较小；3、上班时间自由。', '上升空间比较小，待遇相对外资企业来说要低！ 建议给有能力者提供好的发展空间这样才能留得住人才！', '68', '2010', '1', null, '2012-08-31 22:07:46', '113.124.15.146');
