INSERT INTO `mq_pingjia` VALUES ('919', '14', '12', '16', '10', '10', '10', '10', '68', '还没进去呢，哈哈！在里面参观了一下工作环境，很不错的，每个人一个大工位。', '我觉得联想缺少革命性的产品，类似苹果这种，只有这样才能打败所有人 建议也没接触到，谁知到啥样啊。不过一个个都是人精啊，不好对付。', '139', '2012', '1', null, '2012-08-31 22:09:28', '113.124.15.146');
