INSERT INTO `mq_pingjia` VALUES ('984', '163', '20', '16', '12', '15', '15', '12', '90', '', '', '113', '2011', '3', null, '2012-09-02 00:30:31', '182.40.160.217');
INSERT INTO `mq_pingjia` VALUES ('985', '22', '16', '16', '12', '12', '12', '10', '78', '年假15天；交通补助800；福利不错，有补充商业医疗保险；培训机会较多；国外项目较多，有出国工作的机会；年底奖金还算丰厚；比较人性化', '升职到一定级别就没空间可言了-都是德国人当头头-所以怎么工作都没有太大归属感。即使很多很烂的德国人-比如在他们国家只是中专毕业来了中国也特别牛-给一群清华的同事当头-哪不顺心就一顿骂-看着恶心！德国人即使做错也会推到中国人头上-到了大老板那因为。', '121', '2011', '2', null, '2012-09-02 00:30:52', '182.40.160.217');
INSERT INTO `mq_pingjia` VALUES ('986', '173', '9', '12', '10', '10', '10', '10', '61', '名气比较大，管理体系相对完善。世界500强企业。产品线完善。', '员工激励制度比较差，薪资差异较小。此外，内部发展机会比较少。 建议管理不要过多的以个人为中心', '112', '2009', '2', null, '2012-09-02 00:31:35', '182.40.160.217');
INSERT INTO `mq_pingjia` VALUES ('987', '71', '16', '20', '12', '10', '15', '12', '85', '高成长，相对较高的收益，有很多培训学习的机会。', '累，压力大，很难晋升，中国人在里面比较受歧视的。 建议建立公平合理的晋升机制，能者多劳，多劳多得。', '86', '2012', '1', null, '2012-09-02 00:32:18', '182.40.160.217');
INSERT INTO `mq_pingjia` VALUES ('988', '18', '16', '16', '15', '10', '15', '10', '82', '工作稳定五保齐全，包食宿', '', '138', '2011', '3', null, '2012-09-02 00:33:39', '182.40.160.217');
INSERT INTO `mq_pingjia` VALUES ('989', '83', '20', '20', '15', '15', '15', '15', '100', '管理模式和个人职业发展空间', '工作量大 建议工作环境可以更舒适些', '136', '2012', '2', null, '2012-09-02 00:34:27', '182.40.160.217');
INSERT INTO `mq_pingjia` VALUES ('990', '81', '16', '12', '12', '15', '15', '12', '82', '工作稳定，收入稳定，可以混日子。', '收入没有小公司高，没有小公司自由，限制多。 建议体制不改，管理者是国家任命，建议没用。', '82', '2011', '1', null, '2012-09-02 00:36:01', '182.40.160.217');
INSERT INTO `mq_pingjia` VALUES ('991', '178', '16', '16', '12', '15', '12', '12', '83', '国内外系统的培训，非常好的待遇和福利，非常好的文化氛围，13薪，灵活假期', '有些岗位常年出差，在油田工作。晋升比较稳定，专业也比较窄，会有加班 建议对新人更加注重培养', '89', '2012', '1', null, '2012-09-02 00:36:52', '182.40.160.217');
INSERT INTO `mq_pingjia` VALUES ('992', '7', '16', '12', '12', '15', '15', '12', '82', '公司的各项福利待遇都还不错 公司的各项福利待遇都还不错 公司的各项福利待遇都还不错', '只重视规模 不重视质量 只重视规模 不重视质量 只重视规模 不重视质量 只重视规模 不重视质量 建议步子太大容易扯着蛋 步子太大容易扯着蛋 步子太大容易扯着蛋 步子太大容易扯着蛋', '141', '2011', '2', null, '2012-09-02 00:37:28', '182.40.160.217');
