INSERT INTO `mq_pingjia` VALUES ('1036', '40', '20', '16', '15', '15', '15', '15', '96', '福利待遇很好，单位很稳定，不存在失业的问题，也很轻松，但是工资待遇便低。', '工资水平偏低，不知道什么时候才能够涨工资。 建议公司福利待遇很好，小站上住宿条件很好，就是公资水平都比较低', '96', '2009', '2', null, '2012-09-08 20:02:06', '182.40.0.7');
