INSERT INTO `mq_pingjia` VALUES ('1215', '41', '12', '16', '8', '10', '10', '12', '68', '福利还算可以，工资相对较低，关系在个人发展中相当重要。', '加班多，加班费又低。工作环境较差，职业病的隐患。 建议深入基层，提高工人工资，给员工更多发展机会。', '178', '2012', '3', null, '2012-09-15 13:42:05', '182.40.71.222');
