INSERT INTO `mq_pingjia` VALUES ('1448', '43', '12', '12', '12', '10', '12', '10', '68', '以前文化好，人性化', '官僚，责任感缺失 建议架构调整', '74', '2012', '3', null, '2012-09-22 10:16:44', '182.40.68.62');
INSERT INTO `mq_pingjia` VALUES ('1449', '28', '20', '16', '15', '15', '15', '15', '96', '一个刚毕业的学生值得好好锻炼的地方，工作容量大，压力也大，工资不高', '按年龄和资历评资论辈的情况比较严重，不能给能力强的人更多的机会 建议注重人才的选拔和能力的考察，逐步摆脱等级差异导致的企业弊病', '120', '2009', '4', null, '2012-09-22 10:19:00', '182.40.68.62');
INSERT INTO `mq_pingjia` VALUES ('1450', '73', '9', '12', '10', '10', '10', '10', '61', '比较有前途，工作是八小时制的，只要肯努力就有机遇', '对刚毕业的学生给的工资太低了，才1200,而且从仓储做起，很辛苦 建议提高大学毕业生的基本工资，实行轮岗，多给他们机会', '163', '2011', '1', null, '2012-09-22 10:19:51', '182.40.68.62');
