INSERT INTO `mq_pingjia` VALUES ('1513', '53', '9', '20', '8', '8', '10', '10', '65', '对于刚毕业的人来说工资还可以，促销服装做的不错', '长促与长促之间的关系，长促与短促的关系，复杂，看你遇到什么样的了 建议业绩固然重要，但也要想想留不住人的原因', '71', '2011', '1', null, '2012-09-24 21:15:43', '182.40.76.32');
