INSERT INTO `mq_pingjia` VALUES ('1733', '158', '9', '9', '8', '10', '10', '12', '58', '1、本科新员工实习期六个月，六个月后小三千2、扩展迅速，人才需求量大3、一线据说有一个月开一两万的，但是比较累', '1、比较大的农村个体户2、加班多，必须无条件奉献3、形式主义4、官僚主义 建议1、专人专用，权责明确，各负其责2、避免形式主义和官僚主义', '128', '2012', '4', null, '2012-10-05 19:44:14', '110.232.41.6');
INSERT INTO `mq_pingjia` VALUES ('1734', '7', '6', '20', '6', '6', '6', '6', '50', '平安是世界500强企业，中国保险非国有企业第一名，公司架构比较完整，工作体系较为科学，重视人才的培养和教育。', '在平安工作最好是单身，如果不是单身，别急，早晚变成单身。平安紧张的节奏要求员工把所有的经历和感情都投入进来，真的毁家庭！！ 建议公司是好公司，就是企业内部员工整天勾心斗角，这势必阻碍公司的发展，作为平安员工我想说在平安工作很不开心！！！！！', '86', '2009', '2', null, '2012-10-05 19:45:20', '110.232.41.6');
