INSERT INTO `mq_pingjia` VALUES ('1736', '75', '12', '20', '10', '10', '10', '10', '72', '环境氛围和待遇', '太累 建议人性化管理', '176', '2011', '1', null, '2012-10-05 19:48:54', '110.232.41.6');
INSERT INTO `mq_pingjia` VALUES ('1737', '8', '6', '20', '6', '6', '6', '6', '50', '毕竟还是大公司 福利也不错', '部门没有发展，老板小气 不涨工资，熬死人，老板只注意自己的钱捞的不少。不能同步发展，留不住人 建议不要那么资本家 小气  自私 无聊 作为领导，没有凝聚力', '143', '2009', '1', null, '2012-10-05 19:49:48', '110.232.41.6');
INSERT INTO `mq_pingjia` VALUES ('1738', '120', '16', '12', '12', '12', '12', '12', '76', '管理太严格，有些事管的太细', '管理太严格，有些事管的太细 建议管理太严格，有些事管的太细', '69', '2008', '3', null, '2012-10-05 19:54:47', '110.232.41.6');
INSERT INTO `mq_pingjia` VALUES ('1739', '182', '6', '20', '6', '6', '6', '10', '54', '没发现还有优点', '加班多，而且没有加班费，工作很幸苦，工资也压得低，学不到什么东西，不值得去 建议很大差距', '107', '2011', '1', null, '2012-10-07 12:30:29', '110.232.41.6');
