INSERT INTO `mq_pingjia` VALUES ('1826', '70', '12', '20', '12', '6', '10', '10', '70', '公司对大学生的成长平台很好，不按公司的老员工先入职，只要有才就能升官', '环境恶劣，上班不固定，整体员工工资低，休假时间少，扣工资的理由多 建议给员工多涨涨工资，这样人才流失率会低', '121', '2011', '2', null, '2012-10-10 20:25:35', '110.232.40.185');
