INSERT INTO `mq_pingjia` VALUES ('1924', '13', '12', '20', '10', '12', '10', '10', '74', '海尔的加班真是让人彻底伤心了，宁可晚上给你加班补助也不让早走，那么些人也都在，自己也不好意思走', '为什么要加班，那么些大企业，同样的家电企业人都不加班，就算加班也有加班费，这里算什么，伤心 建议取消加班，加班必须要有加班费，要强制大家到点就下班，禁止 加班', '107', '2011', '3', null, '2012-10-24 20:41:22', '110.232.40.161');
