INSERT INTO `mq_pingjia` VALUES ('1930', '11', '20', '16', '15', '15', '15', '15', '96', '国有银行待遇比较透明也比较稳定，还有就是在一般人的眼中，这是一份体面的工作', '作为合同工-待遇非常底-基本上做的事情比正式员工多-而且累-可是工资连他们的一半都没有-带薪休假什么的那更是浮云了。建议不要把合同工当畜生使-除非想员工一月一换。合同工给银行带来了很多业务-为银行维持形象-而且做的是最辛苦的工作-而这些本来是正式。', '98', '2009', '2', null, '2012-10-24 20:51:16', '110.232.40.161');
